if caseid not in signals:
    result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
else:
    df = signals[caseid][["Time", "SNUADC/PLETH"]].copy()

    # Ensure numeric and sorted by time
    df["Time"] = pd.to_numeric(df["Time"], errors="coerce")
    df["SNUADC/PLETH"] = pd.to_numeric(df["SNUADC/PLETH"], errors="coerce")
    df = df.dropna(subset=["Time"]).sort_values("Time")

    if df.empty:
        result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
    else:
        # Resample to 10 Hz (0.1s) using time-based binning; mean is NaN-resistant
        t0 = df["Time"].iloc[0]
        df["t_rel"] = df["Time"] - t0

        df["t_10hz"] = (np.floor(df["t_rel"] * 10) / 10.0).astype(float)
        ppg_10hz = df.groupby("t_10hz", as_index=False)["SNUADC/PLETH"].mean()

        # Non-overlapping 1-minute windows (60s) on the 10Hz timeline
        ppg_10hz["minute"] = (ppg_10hz["t_10hz"] // 60).astype("int64")
        minute_means = ppg_10hz.groupby("minute")["SNUADC/PLETH"].mean()

        # Count only minutes with at least one non-NaN value
        minute_means_valid = minute_means.dropna()
        minutes_analyzed = int(minute_means_valid.shape[0])

        if minutes_analyzed == 0:
            result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
        else:
            avg_min = float(minute_means_valid.min())
            avg_max = float(minute_means_valid.max())
            result = {"minutes_analyzed": minutes_analyzed, "avg_range": [avg_min, avg_max]}
