        # Resample to 10 Hz (0.1s) using time-based binning and mean aggregation
        t0 = df["Time"].iloc[0]
        t1 = df["Time"].iloc[-1]
        if not np.isfinite(t0) or not np.isfinite(t1) or t1 < t0:
            result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
        else:
            # Create 10Hz bins aligned to the first timestamp
            df["tbin_10hz"] = np.floor((df["Time"] - t0) * 10.0) / 10.0 + t0
            ppg_10hz = df.groupby("tbin_10hz", sort=True)["SNUADC/PLETH"].mean()

            # Non-overlapping 1-minute windows (600 samples at 10Hz)
            if ppg_10hz.empty:
                result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
            else:
                idx = np.arange(ppg_10hz.shape[0])
                minute_id = idx // 600
                minute_means = ppg_10hz.groupby(minute_id).mean()  # NaN-safe mean

                # Count minutes with at least one non-NaN value
                valid_minute_means = minute_means.dropna()
                minutes_analyzed = int(valid_minute_means.shape[0])

                if minutes_analyzed == 0:
                    result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
