    # Remove invalid SBP values: 0 or >= 300 (also drop NaN)
    s = s[(s['Solar8000/ART_SBP'] > 0) & (s['Solar8000/ART_SBP'] < 300)].dropna(subset=['Time', 'Solar8000/ART_SBP'])
    s = s.sort_values('Time')

    if s.empty:
        result = {"rate_of_change_range": [np.nan, np.nan]}
    else:
        # Create datetime index from seconds and resample to 10-second mean
        s['dt'] = pd.to_timedelta(s['Time'], unit='s')
        s = s.set_index('dt')
        sbp_10s = s['Solar8000/ART_SBP'].resample('10s').mean()

        # Non-overlapping 30-minute windows: compute start and end values within each window
        g = sbp_10s.groupby(pd.Grouper(freq='30min'))
        start_vals = g.first()
        end_vals = g.last()

        # Rate of change (%) = (end - start) / start * 100, avoid division by zero
        valid = start_vals.notna() & end_vals.notna() & (start_vals != 0)
        roc = ((end_vals[valid] - start_vals[valid]) / start_vals[valid]) * 100.0
        roc = roc.replace([np.inf, -np.inf], np.nan).dropna()

        if roc.empty:
            result = {"rate_of_change_range": [np.nan, np.nan]}
        else:
            result = {"rate_of_change_range": [float(roc.min()), float(roc.max())]}
