        # Align to 1-second grid: for each integer second, take the last observed value within that second
        df['sec'] = np.floor(df['Time']).astype('int64')
        per_sec = df.groupby('sec', as_index=True)['Solar8000/ART_DBP'].last()

        if per_sec.empty:
            result = {"patient_flag": False, "windows_analyzed": 0}
        else:
            # Create continuous 1-second index and keep NaNs where missing
            sec_index = pd.RangeIndex(int(per_sec.index.min()), int(per_sec.index.max()) + 1, 1)
            per_sec = per_sec.reindex(sec_index)

            # Non-overlapping 10-minute windows (600 seconds) based on seconds since start
            start_sec = int(per_sec.index.min())
            win_id = ((per_sec.index.values - start_sec) // 600).astype('int64')

            win_max = per_sec.groupby(win_id).max()
            win_min = per_sec.groupby(win_id).min()
            win_range = win_max - win_min  # NaN if window has no valid values

            windows_analyzed = int(win_range.notna().sum())
            patient_flag = bool((win_range >= 20).fillna(False).any())

            result = {"patient_flag": patient_flag, "windows_analyzed": windows_analyzed}
