    valid_mask = sbp.notna() & (sbp != 0) & (sbp < 300)
    df2 = df.loc[valid_mask, ['Time']].copy()
    df2['SBP'] = sbp.loc[valid_mask].astype(float)

    if df2.empty:
        result = {"rate_of_change_range": [np.nan, np.nan]}
    else:
        # Create a datetime-like index from seconds for resampling
        df2['dt'] = pd.to_timedelta(df2['Time'].astype(float), unit='s')
        df2 = df2.set_index('dt').sort_index()

        # 10-second mean resampling
        sbp_10s = df2['SBP'].resample('10S').mean()

        # 30-minute non-overlapping windows: compute start/end within each window
        g = sbp_10s.groupby(pd.Grouper(freq='30min'))

        start_vals = g.first()
        end_vals = g.last()

        # Rate of change (%): (end - start) / start * 100
        roc = (end_vals - start_vals) / start_vals * 100.0
        roc = roc.replace([np.inf, -np.inf], np.nan).dropna()

        if roc.empty:
            result = {"rate_of_change_range": [np.nan, np.nan]}
        else:
            result = {"rate_of_change_range": [float(roc.min()), float(roc.max())]}
