    df = signals[caseid]

    # Validate required columns
    if (not isinstance(df, pd.DataFrame)) or ("Time" not in df.columns) or ("SNUADC/PLETH" not in df.columns):
        result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
    else:
        # Ensure numeric
        pleth = pd.to_numeric(df["SNUADC/PLETH"], errors="coerce")
        time = pd.to_numeric(df["Time"], errors="coerce")

        # Minute index based on seconds (1Hz). Use floor(Time/60).
        minute_idx = np.floor(time.values / 60.0).astype("float64")
        minute_idx = pd.Series(minute_idx).where(np.isfinite(minute_idx), np.nan)

        tmp = pd.DataFrame({"minute": minute_idx, "pleth": pleth})
        tmp = tmp.dropna(subset=["minute"])
        tmp["minute"] = tmp["minute"].astype(np.int64)

        # Mean pleth per minute (NaN-safe)
        minute_means = tmp.groupby("minute", sort=True)["pleth"].mean().dropna()

        if minute_means.shape[0] == 0:
            result = {"minutes_analyzed": 0, "avg_range": [np.nan, np.nan]}
        else:
            result = {
                "minutes_analyzed": int(minute_means.shape[0]),
                "avg_range": [float(minute_means.min()), float(minute_means.max())],
            }
