        n = ppg.shape[0]
        win_len = 30  # 30s windows at 1Hz => 30 samples per window
        nwin = n // win_len

        if nwin <= 0:
            result = {"windows": 0, "max_range": [np.nan, np.nan], "min_range": [np.nan, np.nan]}
        else:
            ppg_trim = ppg[: nwin * win_len].reshape(nwin, win_len)

            # Per-window max/min (NaN-resistant)
            w_max = np.nanmax(ppg_trim, axis=1)
            w_min = np.nanmin(ppg_trim, axis=1)

            # If all windows are NaN, nanmin/nanmax will raise; guard with finite check
            finite_max = np.isfinite(w_max)
            finite_min = np.isfinite(w_min)

            if finite_max.any():
                max_range = [float(np.nanmin(w_max)), float(np.nanmax(w_max))]
            else:
                max_range = [np.nan, np.nan]

            if finite_min.any():
                min_range = [float(np.nanmin(w_min)), float(np.nanmax(w_min))]
            else:
                min_range = [np.nan, np.nan]

            result = {"windows": int(nwin), "max_range": max_range, "min_range": min_range}
