            valid = df.dropna(subset=['Solar8000/ART_MBP'])
            if valid.shape[0] < 2:
                result = {"drop_events": 0, "max_drop_percent": float("nan")}
            else:
                x = valid['Time'].values.astype(float)
                y = valid['Solar8000/ART_MBP'].values.astype(float)

                # Remove duplicate times (keep last) to avoid interpolation issues
                order = np.argsort(x)
                x = x[order]
                y = y[order]
                uniq_mask = np.r_[True, x[1:] != x[:-1]]
                x = x[uniq_mask]
                y = y[uniq_mask]

                if x.size < 2:
