                    win_n = int(round(win_sec / 0.5))  # 600 samples
                    step_n = int(round(step_sec / 0.5))  # 120 samples

                    n = s.shape[0]
                    if n < win_n:
                        result = {"drop_events": 0, "max_drop_percent": float("nan")}
                    else:
                        # Build window start indices
                        starts = np.arange(0, n - win_n + 1, step_n, dtype=int)

